package javaTopics;

public class EscapeSequence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("This \n is my new line example");
		System.out.println("This \r is my carriage return example");
		System.out.println("This \t is tab example");
		System.out.println("This \" is my double quote example");
		System.out.println("This \' is my single quote example");
		System.out.println("This \\ is my back slash example");


		
	}

}
